script:
function show_image_section(frm) {
    console.log('show_image_section');
    if(frm.doc.letter_head_name){
        $('div[data-fieldname=image]').removeClass('hide-control');
        $('div[data-fieldname=image]').closest('.row').removeClass('empty-section');
        $('div[data-fieldname=image]').closest('.row').removeClass('hide-control');
        $('div[data-fieldname=image]').closest('.form-column').removeClass('col-sm-2').addClass('col-sm-4');
	}
}

frappe.ui.form.on('Letter Head', {
    refresh(frm) {
        console.log('refresh');
		if(frm.doc.easy_mode===1) {
		    //show_image_section(frm);
        }
	    toggle_fields(frm);
    },
	setup(frm) {
	    console.log('setup');
	    frm.toggle_display(['header_section'], true);
        frm.set_df_property('source', "depends_on", "eval:doc.easy_mode==0");
		frm.set_df_property('letter_head_image_section', "depends_on", "eval:doc.letter_head_name");
        frm.set_df_property('disabled', "depends_on", "eval:doc.letter_head_name && !doc.__islocal");
        frm.set_df_property('is_default', "depends_on", "eval:doc.letter_head_name && !doc.__islocal");

        frm.set_df_property('header_section', "depends_on", "eval:doc.letter_head_name");
        frm.set_df_property('column_break_11', "depends_on", "eval:doc.easy_mode==1");
        frm.set_df_property('123', "depends_on", "eval:doc.easy_mode==1");
        frm.set_df_property('logo_width', "depends_on", "eval:doc.easy_mode==1");
        frm.set_df_property('col3', "depends_on", "eval:doc.easy_mode==1");
        frm.set_df_property('title_size', "depends_on", "eval:doc.easy_mode==1");
        frm.set_df_property('column_break_10', "depends_on", "eval:doc.easy_mode==1");
        frm.set_df_property('text_size', "depends_on", "eval:doc.easy_mode==1");
        frm.set_df_property('text_align', "depends_on", "eval:doc.easy_mode==1");

        if(!frm.doc.letter_head_name){
            frm.doc.letter_head_name = frappe.defaults.get_user_default("company");
    	}

		//frm.trigger("easy_mode");

	},
	easy_mode(frm) {
	    console.log('easy_mode');
	    if(!frm.doc.letter_head_name){
            frappe.throw(__("The name is required"));
    	}

	    toggle_fields(frm);
		if(frm.doc.easy_mode==1) {
		    show_image_section(frm);
		    frm.save();
		}
	},
	after_save(frm){
	    console.log('after_save');
		if(frm.doc.easy_mode==1) {
    		show_image_section(frm);
    	    frm.reload_doc();
		}
    	refresh_field(frm.doc.image);
	},
	source(frm) {
        //frm.toggle_display(['letter_head_image_section', 'header_section'], true);
	},
	business_name(frm) {
	    frappe.model.set_value("business_name", frm.doc.business_name);
	    update_preview(frm);
	},
	address(frm) {
	    frappe.model.set_value("address", frm.doc.address);
	    update_preview(frm);
	},
	city_and_country(frm) {
	    frappe.model.set_value("city_and_country", frm.doc.city_and_country);
	    update_preview(frm);
	},
	phone_number(frm) {
	    frappe.model.set_value("phone_number", frm.doc.phone_number);
		update_preview(frm);
	},
	email(frm) {
	    frappe.model.set_value("email", frm.doc.email);
		update_preview(frm);
	},
	logo_width(frm) {
	    frappe.model.set_value("logo_width", frm.doc.logo_width);
		update_preview(frm);
	},
	text_align(frm) {
	    frappe.model.set_value("text_align", frm.doc.text_align);
		update_preview(frm);
	},
	text_size(frm) {
	    frappe.model.set_value("text_size", frm.doc.text_size);
		update_preview(frm);
	},
	title_size(frm) {
	    frappe.model.set_value("title_size", frm.doc.title_size);
		update_preview(frm);
	}
});

function update_preview(frm) {
	console.log('update_preview');
	let business = frm.doc.business_name;
	let address = frm.doc.address + "<br>";
	let city_country = frm.doc.city_and_country + "<br>";
	let phone =  __("Phone: ") + frm.doc.phone_number + "<br>";
	let email = __("Email: ") + frm.doc.email;

	if (frm.doc.business_name === "" || typeof frm.doc.business_name === 'undefined'){
	    frm.doc.business_name = frm.doc.letter_head_name;
	    business = frm.doc.letter_head_name;
	}
	if (frm.doc.address === "" || typeof frm.doc.address === 'undefined'){
	    address = "";
	}
	if (frm.doc.city_and_country === "" || typeof frm.doc.city_and_country === 'undefined'){
	    city_country = __("City, "+ frappe.defaults.get_user_default("country") +"<br>");
	}
	if (frm.doc.phone_number === '' || typeof frm.doc.phone_number === 'undefined'){
	    phone =  "";
	}
	if (frm.doc.email === '' || typeof frm.doc.email === 'undefined'){
	    email = "";
	}

	let image = "https://thebantoo.com/files/eIeGfxq.png";
    if (frm.doc.image) {
        image = frm.doc.image;
    }

	let html_content = `
		<table width='100%' style='margin-bottom:0; padding-bottom:0;'>
            <tbody>
            <tr>
                <td width='50%' style='vertical-align: top; padding: 1px 0 0 1px;'>
                    <img id='logo' src='`+ image +`'
                        width='`+ frm.doc.logo_width +`' max-height='200' max-width='`+ frm.doc.logo_width +`'/>
                </td>
                <td width='50%' id='addr' style='vertical-align: top; text-align: `+ frm.doc.text_align +`; padding-bottom:0;'>
                    <h`+ frm.doc.title_size +`>`+ business +`</h`+ frm.doc.title_size +`>
                    <p style='font-size:`+ frm.doc.text_size +`;'>`+ address +`
                    `+ city_country +`
                    ` + phone +`
                    ` + email +`
                    </p>
                </td>
            </tr>
            </tbody>
        </table>`;
	frm.doc.content = html_content;
	refresh_field('content');
	show_image_section(frm);
}

function toggle_fields(frm){
    console.log('toggle_fields');
    if(frm.doc.__islocal){
        frm.doc.is_default = 1;
		$("select[data-fieldname=source]").val("HTML").trigger('change');
    	$(".html-preview").show();
        frm.toggle_display(['letter_head_image_section', 'header_section'], true);
    }

    if(frm.doc.easy_mode===1) {
	    $("button:contains('Preview'):first").trigger('click');
    	$(".section-head:contains('Header')").hide();
    	$("p:contains('Letter Head in HTML')").hide();
    	$("label:contains('Header HTML')").parent().hide();
    	$("button:contains('Edit'):first").hide();
    	$("button:contains('Expand'):first").hide();
    	$(".html-preview").css({'max-height': '220px', 'min-height': '200px'});
    }
    else {
	    $("button:contains('Edit'):first").trigger('click');
    	$(".section-head:contains('Header')").show();
    	$("p:contains('Letter Head in HTML')").show();
    	$("label:contains('Header HTML')").parent().show();
    	$("button:contains('Edit'):first").show();
    	$("button:contains('Expand'):first").show();
    	$(".html-preview").css({'max-height': '600px', 'min-height': '260px'});
    }

    show_image_section(frm);
    update_preview(frm);

}
